import { useEffect, useState  } from 'react';
import './App.css';

function GameScreen(props) {
    const [gameTime, setGameTime] = useState(10)
    const [gameScore, setGameScore] = useState(0)
    const [prompt, setPrompt] = useState('')
    const [answer, setAnswer] = useState('')
    const [inputError, setInputError] = useState(false)
    const [inputErrorMessage, setInputErrorMessage] = useState('')
    const [promptPool, setPromptPool] = useState([])
    const [validateUrl, setValidateUrl] = useState('')

    const loadPrompt = () => {
        let index = Math.floor(Math.random()*500)
        let indexWord = promptPool[index].word
        let sliceStart = Math.floor(Math.random() * 3) + 1
        let thisPrompt = indexWord.substring(sliceStart, sliceStart+3)
        setPrompt(thisPrompt)
    }

    async function getPromptPool() {
        await (fetch("https://api.datamuse.com/words?sp=???????&max=500"))
        .then(response => {return response.json()}
        ).then(response => {
            setPromptPool(response)
        })
    }

    useEffect(() => {
        getPromptPool()
    }, [])

    useEffect(() => {
        if(promptPool.length > 0) {
            loadPrompt()
        }
    }, [promptPool])

    useEffect(() => {
        setAnswer(answer)
        setValidateUrl('https://api.dictionaryapi.dev/api/v2/entries/en/'+answer)
        console.log(validateUrl)
        console.log(answer)
    }, [answer])

    function handleKeyDown(e) {
            if(e.keyCode === 13) {
                validate(answer)
            }
      }

    useEffect(() => {
        document.addEventListener('keydown', handleKeyDown);
      }, []);

    async function validate(word) {
        const start = word.slice(0,3)
        const end = word.slice(word.length-3, word.length)
        const newPrompt = prompt

        await (fetch(validateUrl)
            .then(response => {
                return response.text();
            })
            .then(response => {
                if ((!response.title) && (start !== newPrompt) && (end !== newPrompt) && word.includes(newPrompt)) {
                    setGameScore(gameScore++)
                    setGameTime(10)
                    setInputError(false)
                    setInputErrorMessage('')
                    loadPrompt()
                } else {
                    if(response.title) {
                        setInputErrorMessage('Not a Word!')
                        setInputError(true)
                    } else if (start == newPrompt) {
                        setInputErrorMessage('Answer cannot start with the prompt!')
                        setInputError(true)
                    } else if (end == newPrompt) {
                        setInputErrorMessage('Answer cannot end with the prompt!')
                        setInputError(true)
                    } else if (!word.includes(newPrompt)){
                        setInputErrorMessage('Answer must contain the prompt!')
                        setInputError(true)
                    }
                }
            }))
    }

    // loadPrompt() {
    //     await fetch("https://random-word-api.herokuapp.com/word?lang=en")
    //         .then(response => {
    //             return response.json()
    //         })
    //         .then(word => {
    //             let newPrompt = word[0].substring(2,5)
    //             setPrompt(newPrompt)
    //     });


    // }

    function ShowInputError({bool, errorMessage}) {
        if (bool == true) {
            return (
                <div className='inputError'>
                    <text style={{fontSize: '2em'}}>{errorMessage}</text>
                </div>
            )
        }
    }

  return (
    <div className="container">
      <span className='topper'>WHWG</span>
        <div className='gameBox'>
            <div className='attributesContainer'>
                <div className='featureBox'>
                    <text className='timeScoreText'>TIME</text>
                    <text className='timeText'>{gameTime}</text>
                </div>
                <div className='featureBoxPrompt'>
                    <text className='promptText'>[{prompt}]</text>
                </div>
                <div className='featureBox'>
                    <text className='timeScoreText'>SCORE</text>
                    <text className='timeText'>{gameScore}</text>
                </div>
            </div>
            <div className='inputContainer'>
                <div>
                    <span style={{fontSize: '6em'}}>[</span>
                    <input className='answerInput' onChange={e => setAnswer(e.target.value)} value={answer}></input>
                    <span style={{fontSize: '6em'}}>]</span>
                </div>
                <ShowInputError bool={inputError} errorMessage={inputErrorMessage}/>
            </div>
        </div>
    </div>
  );
}

export default GameScreen;
