import { useEffect, useState  } from 'react';
import './App.css';

function GameScreen(props) {
    const [gameTime, setGameTime] = useState(10)
    const [gameScore, setGameScore] = useState(0)
    const [prompt, setPrompt] = useState('')
    const [answer, setAnswer] = useState('')
    const [inputError, setInputError] = useState(true)

    useEffect(() => {
        setTimeout(loadPrompt, 1000)
    }, [])

    useEffect(() => {
        function handleKeyDown(e) {
          console.log(e.keyCode);
        }

        document.addEventListener('keydown', handleKeyDown);

        // Don't forget to clean up
        return function cleanup() {
          document.removeEventListener('keydown', handleKeyDown);
        }
      }, []);

    const changeAnswer = (ans) => {
        setAnswer(ans.target.value)
    }

    async function validate(word) {
        let start = word.slice(0,3)
        let end = word.slice(word.length-3, word.length)
        let newPrompt = prompt

        await (fetch("https://api.dictionaryapi.dev/api/v2/entries/en/"+word)
            .then(response => {
                return response.json();
            })
            .then(response => {
                if ((!response.title) && (start !== newPrompt) && (end !== newPrompt) && word.includes(newPrompt)) {
                    setGameScore(gameScore++)
                    setGameTime(10)
                    setAnswer('')
                    loadPrompt()
                } else {

                }
            }))
    }

    async function loadPrompt() {
        await fetch("https://random-word-api.herokuapp.com/word?lang=en")
            .then(response => {
                return response.json()
            })
            .then(word => {
                let newPrompt = word[0].substring(2,5)
                setPrompt(newPrompt)
        });


    }

    function ShowInputError({bool}) {
        return (
            <div className='inputError'>
                <text style={{fontSize: '2em'}}>not a word!</text>
            </div>
        )
    }

  return (
    <div className="container">
      <span className='topper'>WHWG</span>
        <div className='gameBox'>
            <div className='attributesContainer'>
                <div className='featureBox'>
                    <text className='timeScoreText'>TIME</text>
                    <text className='timeText'>{gameTime}</text>
                </div>
                <div className='featureBoxPrompt'>
                    <text className='promptText'>[{prompt}]</text>
                </div>
                <div className='featureBox'>
                    <text className='timeScoreText'>SCORE</text>
                    <text className='timeText'>{gameScore}</text>
                </div>
            </div>
            <div className='inputContainer'>
                <div>
                    <span style={{fontSize: '6em'}}>[</span>
                    <input className='answerInput' onChange={e => changeAnswer(e)} value={answer}></input>
                    <span style={{fontSize: '6em'}}>]</span>
                </div>
                <ShowInputError bool={inputError} />
            </div>
        </div>
    </div>
  );
}

export default GameScreen;
