import { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';

function Scoreboard(props) {
    const [scoreData, setScoreData] = useState([])

    const getScores = () => {
        axios.get('http://localhost:8080/scores').then((data) => {
            console.log(data)
            setScoreData(data)
        })
    }

    useEffect(() => {
        const fetchData = async () =>{
            try {
              const {data: response} = await axios.get('http://localhost:8080/scores');
              setScoreData(response);
            } catch (error) {
              console.error(error.message);
            }
          }

        fetchData()
    }, [])

  return (
    <div className="container">
      <span className='topper'>WHWG</span>
        <div style={{paddingTop: '3%', paddingBottom: '2%'}}>
          <text style={{fontSize: '2em'}} >Scoreboard</text>
        </div>
        <div style={{paddingBottom: '1%'}}>
          <text style={{fontSize: '2em', paddingRight: '5%'}} >Name</text>
          <text style={{fontSize: '2em', paddingLeft: '5%'}} >Score</text>
        </div>
        <div style={{ height: '60%', width: '30%', overflowY: 'scroll', border: '1px solid black', marginLeft: '35%'}}>
            {scoreData}
        </div>
    </div>
  );
}

export default Scoreboard;
